mapper.select:
select count(*)
from comm_board




				and user_no=#{searchtxt}				


				and upper(content_hate) like '%'|| upper(#{searchtxt})||'%'				


				and content_like=#{searchtxt}


select user_id, content_date, content_content, content_like, content_hate
from comm_board
where content_no=#{contentno}
