mapper.select:
select count(*)
from comm_board
where board_distinct_no = #{boardno} and movieCd = #{movieCd}

select content_no, user_id, e1.user_no, e1.movieCd, board_distinct_no, content_date, content_content, content_like, content_hate, spoiler_tf from comm_board e1 left join user_info e2 on e1.user_no=e2.user_no
left join movie_info e3 on e1.movieCd=e3.movieCd
where board_distinct_no = 1 and e1.movieCd = #{movieCd} order by content_date DESC limit #{startRow}, #{pageSize}

mapper.update:
update comm_board
set content_like = content_like+1
where content_no = #{contentno}

update comm_board
set content_hate = content_hate+1
where content_no = #{contentno}

update comm_board
set content_content = #{contents}
where content_no = #{contentno}

mapper.select:
select content_no, report_content
from report
where content_no = #{contentno} and report_content = #{reportreason}

mapper.update:
update report
set report_count = report_count+1
where content_no = #{contentno} and report_content = #{reportreason}

update comm_board
set content_title = #{content_title}
, content_content=#{content_content}
where content_no = #{content_no}
